(function() {
  var burger = document.querySelector('.burger');
  var nav = document.querySelector('#'+burger.dataset.target);
 
  burger.addEventListener('click', function(){
    burger.classList.toggle('is-active');
    nav.classList.toggle('is-active');
  });
})();


document.getElementById("btnEnviar").addEventListener('click', function ExibirNotificação (e)
{
  e.preventDefault();
  var notificacao = document.getElementsByClassName("notificacao");
  notificacao[0].classList.remove("is-invisible");

  var nome = document.getElementById("nome").value;
  document.getElementById("pessoa").innerHTML = nome;   

})

document.getElementById("delete").addEventListener('click', function Esconder (){
  var notificacao = document.getElementsByClassName("notificacao");    
  notificacao[0].classList.add("is-invisible");
} )


